mapper.select:
SELECT BOARD_NO as boardNo, BOARD_TITLE as boardTitle, BOARD_CONTENT as boardContent, BOARD_REGDATE as boardRegdate, BOARD_COUNT as boardCount, BOARD_ENDYN as boardEndYN, BOARDPHOTO_NO as boardPhotoNo FROM (SELECT ROW_NUMBER() OVER(ORDER BY BOARD_NO DESC)AS NUM, BOARD.* FROM BOARD WHERE BOARD_ENDYN='N'
AND BOARD_TITLE LIKE '%'||#{keyword}||'%'
AND BOARD_CONTENT LIKE '%'||#{keyword}||'%'
AND BOARD_TITLE LIKE '%'||#{keyword}||'%' OR BOARD_CONTENT LIKE '%'||#{keyword}||'%'
) WHERE NUM BETWEEN #{start} AND #{end}
